@bot.message_handler(commands=['start'])
def start(message):
    markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
    btn1 = types.KeyboardButton("üëã –ü–æ–∑–¥–æ—Ä–æ–≤–∞—Ç—å—Å—è")
    btn2 = types.KeyboardButton("‚ùì –æ—Ñ–æ—Ä–º–∏—Ç—å –≤–æ–ø—Ä–æ—Å")
    btn3 = types.KeyboardButton('üìì –í–∏–∫–µ–ø–µ–¥–∏—è')
    btn4 = types.KeyboardButton('/help')
    markup.add(btn1, btn2, btn3, btn4)
    bot.send_message(message.chat.id, text="–î–æ–±—Ä–æ –ø–æ–∂–∞–ª–æ–≤–∞—Ç—å, {0.first_name}, —ç—Ç–æ —ç–∫–æ–ª–æ–≥–∏—á–µ—Å–∫–∏–π –±–æ—Ç!".format(
        message.from_user), reply_markup=markup)


@bot.message_handler(commands=['help'])
def help(message):
    sticker = open('img/eco5.png', 'rb')
    markup = types.ReplyKeyboardMarkup(resize_keyboard=True)
    btnhelp1 = types.KeyboardButton('–£–∑–Ω–∞—Ç—å –∏–Ω—Ñ–æ—Ä–º–∞—Ü–∏—é –æ –±–æ—Ç–µ?')
    btnhelp2 = types.KeyboardButton('–ö–∞–∫ –ø–æ–¥–¥–µ—Ä–∂–∞—Ç—å –ó–µ–º–ª—é?')
    markup.add(btnhelp1, btnhelp2)
    bot.send_message(message.chat.id, text='–ß–µ–º —è –º–æ–≥—É –ø–æ–º–æ—á—å –≤–∞–º? {0.first_name}'.format(message.from_user),
                     reply_markup=markup)
    bot.send_sticker(message.chat.id, sticker)


